<script>
  var config = {
    'maps':{
      'regions':<?php echo json_encode($regions); ?>
    }
  }

  jQuery(function(){

    (function(){

      jQuery('#header').hide('');

      var regions = config.maps.regions;
      var map;
      var infowindow = new google.maps.InfoWindow();

      (function(){

        var myOptions = {
          'zoom': 6,
          'center': new google.maps.LatLng(52.013525674017004, 19.01123046875),
          'mapTypeId': google.maps.MapTypeId.ROADMAP,

          'disableDefaultUI': true,

          scrollwheel: false

        };

        map = new google.maps.Map(document.getElementById('map'), myOptions);
      })();


      for(i in regions){
        var points = new Array();
        var data = regions[i]['data'];
        var html = regions[i]['cloud']['html'];

        for(j in data){
          points.push( new google.maps.LatLng(data[j]['y'],data[j]['x']) );
        }

        var polygon = new google.maps.Polygon({
          'paths': points,
          'strokeColor': "#008837",
          'strokeOpacity': 1,
          'strokeWeight': 1,
          'fillColor': "#75DB0F",
          'fillOpacity': 0.35,
          'html': html
        });

        polygon.setMap(map);

        google.maps.event.addListener(polygon, 'click', function(point) {

            jQuery(this).remove()

          infowindow.close(); // zamykamy ewentualne wcześniejsze chmurki
          infowindow = new google.maps.InfoWindow({
            'content':  this.html,
            'position': new google.maps.LatLng( point.latLng.lat(), point.latLng.lng() )
          });
          infowindow.open(map);
        });
      } // end For

    })()

  });
</script>
