echo "select update_date,email,first_name,last_name,phone_number,mobile_number,tax_office_poland_id,powiat.name as powiat,work_city,work_name from user_profile left join users on users.id=user_id left join poland as gmina on work_poland_id=gmina.id left join poland as powiat on gmina.parent_id=powiat.id where email !~ 'promienko.pl' and email !~ 'eszkola-wielkopolska' order by update_date; " | psql91 -d eszkola_wielkopolska_pl -h database -U postgres -q | sed "s/|/;/g" > profile.csv
